mapper.update:
update poseidon.db_shop_cart_item
set item_count = #{count}
where shop_cart_id = #{shop_cart_id}
  and item_id = #{item_id}

mapper.delete:
delete
from poseidon.db_shop_cart_item
where shop_cart_id = #{shop_cart_id}
and item_id in

    #{itemId}

mapper.select:
select shop_cart_id,item_id,item_count
from poseidon.db_shop_cart_item
where shop_cart_id = #{shop_cart_id}

select item_count
from poseidon.db_shop_cart_item
where shop_cart_id = #{shop_cart_id}
  and item_id = #{itemId}
